INSERT INTO SYSTEM_SIGHELP (LCODE, STRTITLE, STRATKTYPE, BSEVERITY, STRSUMMARY, STRDESCRIPTION, STRFALSEPOSITIVE, STRSOLUTION, STRREFERENCE, STRCVEID, STRBID, STRVUL, STRNOTVUL, STRADDRSPOOF, STRDETECT) VALUES(3421,'http phpMyadmin cookie.auth.lib.php Cross-Site Scripting','Web_Access ',3,'phpMyadmin 크로스 사이트 스크립팅 취약성을 이용한 공격이 시도되었습니다.','"phpMyAdmin은 MySQL 관리 작업을 처리하는데 사용할 수 있는 웹 인터페이스를 제공하는 무료 도구입니다.
phpMyAdmin에 크로스 사이트 스크립팅 취약성이 있습니다. 이 취약성은 애플리케이션이 사용자가 libraries/auth/cookie.auth.lib.php 스크립트의 매개변수에 제공한 입력 값을 제대로 검사하지 않기 때문에 나타납니다."','이 시그니처는 /cookie.auth.lib.php의 HTTP 요청을 탐지하므로, 해당 페이지에 대한 정상적인 접근이 탐지될 수 있습니다. 매개변수의 입력값이 정상인지, 추가적인 검사를 수행하여  사용자가 취약한 페이지에 접근한 의도를 파악하십시오. ','소프트웨어를 최신 버전(phpmyadmin v2.6.1-pl2 이상)으로 업그레이드 하고, 벤더가 제공하는 최신 패치를 적용합니다.','http://secunia.com/advisories/16605/','','','phpMyAdmin 2.x','','','alert tcp any any -> any 80 (flow:to_server,established; uricontent:"/cookie.auth.lib.php"; nocase; rev:1;)');
INSERT INTO SYSTEM_SIGHELP (LCODE, STRTITLE, STRATKTYPE, BSEVERITY, STRSUMMARY, STRDESCRIPTION, STRFALSEPOSITIVE, STRSOLUTION, STRREFERENCE, STRCVEID, STRBID, STRVUL, STRNOTVUL, STRADDRSPOOF, STRDETECT) VALUES(3422,'http phpMyadmin error.php Cross-Site Scripting','Web_Access ',3,'phpMyadmin 크로스 사이트 스크립팅 취약성을 이용한 공격이 시도되었습니다.','"phpMyAdmin은 MySQL 관리 작업을 처리하는데 사용할 수 있는 웹 인터페이스를 제공하는 무료 도구입니다.
