INSERT INTO SYSTEM_SIGHELP (LCODE, STRTITLE, STRATKTYPE, BSEVERITY, STRSUMMARY, STRDESCRIPTION, STRFALSEPOSITIVE, STRSOLUTION, STRREFERENCE, STRCVEID, STRBID, STRVUL, STRNOTVUL, STRADDRSPOOF, STRDETECT) VALUES(4180,'http known malicious js file access using iframe tag','Web_Access ',3,'html 파일의 iframe 태그에서 악의적인 것으로 알려진 js 스크립트 파일에 접근하는 시도가 탐지되었습니다.','본 시그니처는 전송된 html 페이지의 iframe 태그에 악의적인 것으로 알려진 js 파일이 존재하는 경우를 탐지합니다. 이 악의적인 js 파일들은 2008년 한 해 동안 발생한 Mass SQL Injection 공격으로 인해 매우 많은 서버의 DB에 삽입되어 있습니다. 이 공격에 주로 사용된 js 파일은 a.js, 1.js, fuckjp.js 등입니다.','해당 시그니처에 대한 오탐 정보가 없습니다. 오탐신고 및 시그니처에 관한 기타 문의 사항은 (주)정보보호기술  CERT 팀 (cert@infosec.co.kr) 으로 연락 주시기 바랍니다.       ','외부로 서비스를 제공할 필요가 없는 경우, 네트워크 경계에서 외부로부터의 엑세스를 차단합니다. 모든 소프트웨어를 비특권 사용자로 실행합니다. 네트워크 기반 침임 탐지 시스템을 사용하여, 악의적인 웹 트래픽을 모니터링합니다. 웹 서버도 이런 요청들을 로그에 남기므로 서버의 로그 파일을 감사합니다. ','','','','','','','alert tcp any 80 -> any any (content:"|3c|iframe|20|src"; nocase; pcre:"/\x3ciframe\x20src=\x22http/i"; pcre:"/\/(1\.js|0\.js|m\.js|jp\.js|b\.js|vip\.js|s\.js|tian\.js|014\.js|bf\.js|real\.js|g\.js|f\.js|7\.js|a\.js|ip\.js|5\.js|fuckjp\.js|fjp\.js|linkcode\.js|2\.js|3\.js|4\.js\x22)/iR"; pcre:"/\x3c\/iframe\x3e/iR"; rev:1;)');
INSERT INTO SYSTEM_SIGHELP (LCODE, STRTITLE, STRATKTYPE, BSEVERITY, STRSUMMARY, STRDESCRIPTION, STRFALSEPOSITIVE, STRSOLUTION, STRREFERENCE, STRCVEID, STRBID, STRVUL, STRNOTVUL, STRADDRSPOOF, STRDETECT) VALUES(4183,'http possible cookie tampering attack attempt - command','Web_Access',3,'웹 서버로 악의적인 쿠키 값이 포함된 HTTP 요청이 전송되었다.','쿠키는 특수한 목적을 가지고 웹 서버와 클라이언트가 주고 받는 매우 작은 텍스트 파일이다. 공격자가 이 쿠키를 조작한 후 웹 서버에 제공하는 기법으로 명령 실행 공격을 수행할 수 있다. 본 시그니처는 웹 서버로 전송되는 HTTP 요청의 Cookie 헤더에 악의적인 명령 실행 구문이 포함되어 있는 경우를 탐지한다.','','','','" "','" "','" "','','','alert tcp any any -> any 80 (content:"Cookie|3a20|"; offset:100; nocase; pcre:"/^Cookie\x3a\x20[^\r\n]{1,300}(ipconfig|ifconfig|netstat|uname|mkdir)[^\r\n]{1,300}\x0d\x0a/im"; rev:1;)');
INSERT INTO SYSTEM_SIGHELP (LCODE, STRTITLE, STRATKTYPE, BSEVERITY, STRSUMMARY, STRDESCRIPTION, STRFALSEPOSITIVE, STRSOLUTION, STRREFERENCE, STRCVEID, STRBID, STRVUL, STRNOTVUL, STRADDRSPOOF, STRDETECT) VALUES(4189,'icmp ping MS08-067 Win32/Trojan Gimmiv','Information',5,'MS08-067 취약점을 이용하여 전파하는 웜의 감염 시도가 탐지되었습니다.','Microsoft Server Service에서 원격 코드 실행 취약점이 발견되었습니다. 이 취약점은 공격자가 특수하게 조작된 형식의 RPC 요청을 전송하는 기법으로 공격할 수 있습니다. MS08-067 취약점을 공격하여 자신을 전파하는 Gimmiv 웜은 원격의 CNC 호스트와 통신할 때 특수한 형태의 ICMP Ping 이벤트를 전송합니다. 본 이벤트는 이와 같은 형태의 패킷을 탐지합니다.','해당 시그니처에 대한 오탐 정보가 없습니다. 오탐신고 및 시그니처에 관한 기타 문의 사항은 (주)정보보호기술  CERT 팀 (cert@infosec.co.kr) 으로 연락 주시기 바랍니다.       ','"네트워크 경계에서 TCP/139 및 445 포트에 대한 접근을 차단합니다.

네트워크에 연결된 컴퓨터에 호스트 기반 방화벽을 설치하여 운용하시기 바랍니다.

네트워크에 연결된 컴퓨터에 안티 바이러스 제품을 설치하고 최신 시그니처로 업데이트 하시기 바랍니다."','"http://www.microsoft.com/technet/security/Bulletin/MS08-067.mspx

