INSERT INTO SYSTEM_SIGHELP (LCODE, STRTITLE, STRATKTYPE, BSEVERITY, STRSUMMARY, STRDESCRIPTION, STRFALSEPOSITIVE, STRSOLUTION, STRREFERENCE, STRCVEID, STRBID, STRVUL, STRNOTVUL, STRADDRSPOOF, STRDETECT) VALUES(4423,'http possible DDoS CC attack','Web_Access ',3,'호스트에서 페이지 요청시 cache-control로 no-store, must-revalidate 값이 전송되는 것을 탐지 하였습니다.','"""no-store옵션으로 클라이언트는 캐쉬값을 저장하지 않으며
요청되는 페이지는 항상 새롭게 요청됩니다.
클라이언트가 개쉬값을 가지고 있지 않거나 가지고 있으나 시간이 만료될 경우 validation을 수행합니다.
must-revalidion 요청으로 인해 캐쉬는 매번 revalidation을 수행해야 합니다. 따라서 서버에 걸리는 부하가 커져서 특정시간 임계값을 초과할 경우 서버의 큐가 꽉 참으로 인해 서비스 불가 상태에 이를 수 있습니다. 특정시간에 이 요청이 임계값 이상으로 요청되었기 때문에 DDOS 공격으로 판단됩니다."""','해당 시그니처에 대한 오탐 정보가 없습니다. 오탐신고 및 시그니처에 관한 기타 문의 사항은 (주)정보보호기술  CERT 팀 (cert@infosec.co.kr) 으로 연락 주시기 바랍니다.     ','"""must-revalidation은 추천되지 않은 값임에도 불구하고 user agents 가 명령을
내릴경우 user에게 유효하지 않은 응답이 제공되었다는 경고를 주어야합니다. 서버의 큐를 늘리고 해당 공격이 들어오는 출발지 IP를 차단합니다."""','http://www.ietf.org/rfc/rfc2616.txt','','','','','','alert tcp any any -> any 80 (flow:established,to_server; content:"Cache-Control|3a20|no-store, must-revalidate"; nocase; rev:1;)');
INSERT INTO SYSTEM_SIGHELP (LCODE, STRTITLE, STRATKTYPE, BSEVERITY, STRSUMMARY, STRDESCRIPTION, STRFALSEPOSITIVE, STRSOLUTION, STRREFERENCE, STRCVEID, STRBID, STRVUL, STRNOTVUL, STRADDRSPOOF, STRDETECT) VALUES(4429,'backdoor.Win32.Hupigon.gavt CaC server communication attempt','Access_Attempt',3,'Backdoor.Win32.Hupigon.gavt가 감염된 호스트와 CaC서버와의 통신이 탐지 되었습니다.','해당 이벤트는 Backdoor.Win32.Hupigon.gavt가 감염된 호스트와 CaC서버와 통신하는 활동이며 CaC의 명령을 받아 악의적인 활동을 할 수 있습니다.','해당 시그니처에 대한 오탐 정보가 없습니다. 오탐신고 및 시그니처에 관한 기타 문의 사항은 (주)정보보호기술  CERT 팀 (cert@infosec.co.kr) 으로 연락 주시기 바랍니다.     ','네트워크를 모니터링하여 백도어에 감염된 호스트와 특정 서버와의 통신을 탐지하여 백도어에 감염된 호스트와 서버를 차단하고 백신을 최신으로 업데이트하여 감염된 호스트의 백도어를 제거합니다.','http://www.virustotal.com/analisis/373b664fb0d1a5e9957769f854bfea25','','','','','','alert tcp any any -> any any (content:"HTTP|0b|46170"; nocase; rev:1;)');
INSERT INTO SYSTEM_SIGHELP (LCODE, STRTITLE, STRATKTYPE, BSEVERITY, STRSUMMARY, STRDESCRIPTION, STRFALSEPOSITIVE, STRSOLUTION, STRREFERENCE, STRCVEID, STRBID, STRVUL, STRNOTVUL, STRADDRSPOOF, STRDETECT) VALUES(4441,'backdoor netbot attacker ip update server dns query','Access_Attempt',3,'netbot의 좀비들의 정보를 제공하는 server로의 DNS 쿼리가 발견되었습니다.','netbot은 botnet 프로그램의 한 종류로 bot에 감염된 좀비들이 C&C server의 명령을 받아 여러가지 기능을 수행합니다. 여기에는 DDOS뿐아니라 원격제어, 파일보기, 레지스트리 관리 등이 있습니다. 해당 DNS 서버는 중국의 서버로서 netbot을 사용하고자 하는 사용자에게 좀비ip정보를 제공합니다. 즉, 이 경고는 누군가가 netbot을 이용하기 위해 좀비 ip 제공 서버로 접근하려 함을 의미합니다.','해당 시그니처에 대한 오탐 정보가 없습니다. 오탐신고 및 시그니처에 관한 기타 문의 사항은 (주)정보보호기술  CERT 팀 (cert@infosec.co.kr) 으로 연락 주시기 바랍니다.     ','이 경고가 발생할 경우 네트워크를 모니터링하여 해당 호스트를 차단하고 ACL을 적용하여 해당 도메인을 차단할 것을 권고합니다.','http://www.nbddos.com/','','','','','','alert udp any any -> any 53 (content:"|08|hackeroo|06|netbot|03|com|02|cn|00|"; nocase;)');
INSERT INTO SYSTEM_SIGHELP (LCODE, STRTITLE, STRATKTYPE, BSEVERITY, STRSUMMARY, STRDESCRIPTION, STRFALSEPOSITIVE, STRSOLUTION, STRREFERENCE, STRCVEID, STRBID, STRVUL, STRNOTVUL, STRADDRSPOOF, STRDETECT) VALUES(4442,'backdoor famous botnet ddns dns query','Access_Attempt',3,'3322.org 도메인 쿼리가 발견되었습니다.','3322.org는 중국의 ddns 서비스 업체로 봇넷 C&C서버의 도메인으로 가장많이 쓰이는 DNS 서비스 업체입니다. 즉, 이 도메인을 사용하는 봇넷의 C&C서버는 특정단어 뒤에 3322.org가 붙는다. 특정 ISP업체에서는 이 도메인을 막아놓고 있습니다. 올바른 사용의 경우가 드물뿐 아니라 악의적인 DNS 쿼리로 판단 할 수 있습니다.','해당 시그니처에 대한 오탐 정보가 없습니다. 오탐신고 및 시그니처에 관한 기타 문의 사항은 (주)정보보호기술  CERT 팀 (cert@infosec.co.kr) 으로 연락 주시기 바랍니다.     ','이 경고가 발생할 올바른 도메인 사용의 경우가 아니라면 해당 호스트를 차단하고 백신을 최신으로 업데이트 하여 봇을 제거합니다.','http://www.3322.org/','','','','','','alert udp any any -> any 53 (content:"|04|3322|03|org|00|"; nocase;)');
INSERT INTO SYSTEM_SIGHELP (LCODE, STRTITLE, STRATKTYPE, BSEVERITY, STRSUMMARY, STRDESCRIPTION, STRFALSEPOSITIVE, STRSOLUTION, STRREFERENCE, STRCVEID, STRBID, STRVUL, STRNOTVUL, STRADDRSPOOF, STRDETECT) VALUES(4444,'http Known Malicious User-Agent String - Toata dragostea','Web_Access ',3,'비정상적인 User-Agent 문자열을 사용하는 HTTP 요청이 전송되었습니다.','"""HTTP 요청에서 User-Agent 헤더 값은 일반적으로 서버가 클라이언트에서 사용하는 프로그램을 식별할 수 있도록 클라이언트가 서버에게 전송하는 텍스트 문자열을 지칭합니다.
본 시그니처는 일반적인 브라우저나 메일 클라이언트등에서 사용되지 않는 User-Agent 문자열이 전송되는 것을 탐지합니다.
이 시그니처가 탐지하는 User-Agent 값은 ReadFileURL다.
외부에서 내부로 전송되는 HTTP 요청에 비정상적인 User-Agent 문자열이 사용"""','해당 시그니처에 대한 오탐 정보가 없습니다. 오탐신고 및 시그니처에 관한 기타 문의 사항은 (주)정보보호기술  CERT 팀 (cert@infosec.co.kr) 으로 연락 주시기 바랍니다.     ','"""내부에서 외부로 전송되는 HTTP 요청에 비정상적인 User-Agent 문자열이 사용되었으면,
악성 코드나 백도어, 스캐너등에 의해 비정상적인 네트워크 요청이 발생한 것으로 간주할 수 있습니다.
따라서, Source IP에서 비정상적인 네트워크 요청을 발생시키는 원인을 파악하고 사후 조치를 취하도록 합니다."""','"""http://dmitry-dulepov.com/article/toata-dragostea-mea-pentru-diavola.html
http://www.oziel.org/usage/agent_200903.html"""','','','','','','alert tcp any any -> any 80 (flow:established,to_server; content:"|0d0a|User-Agent|3a20|Toata dragostea mea pentru diavola|0d0a|"; nocase;)');
INSERT INTO SYSTEM_SIGHELP (LCODE, STRTITLE, STRATKTYPE, BSEVERITY, STRSUMMARY, STRDESCRIPTION, STRFALSEPOSITIVE, STRSOLUTION, STRREFERENCE, STRCVEID, STRBID, STRVUL, STRNOTVUL, STRADDRSPOOF, STRDETECT) VALUES(4446,'Elevation of Privilege file download attempt - MS09-012','Web_Access ',3,' MS09-012 취약점을 이용한 실행파일을 다운로드하는 행위가 탐지되었습니다.',' Microsoft Windows 플랫폼의 MSDTC(Microsoft Distributed Transaction Coordinator) 트랜잭션 기능에 권한 상승 취약점이 존재합니다. MSDTC는 호출하는 모든 프로세스가 가장할 수 있도록 NetworkService 토큰을 제공합니다. 이 취약점을 악용하면 NetworkService 계정으로 실행되지 않았지만 SeImpersonatePrivilege를 보유한 프로세스의 권한을 NetworkService로 상승시켜 NetworkService 권한으로 코드를 실행할 수 있습니다. 이 취약점 악용에 성공한 공격자는 임의 코드를 실행하여 영향을 받는 시스템을 완전히 제어할 수 있습니다. 이렇게 되면 공격자가 프로그램을 설치할 수 있을 뿐 아니라 데이터를 보거나 변경하거나 삭제할 수 있고 모든 사용자 권한이 있는 새 계정을 만들 수도 있습니다.','해당 시그니처에 대한 오탐 정보가 없습니다. 오탐신고 및 시그니처에 관한 기타 문의 사항은 (주)정보보호기술  CERT 팀 (cert@infosec.co.kr) 으로 연락 주시기 바랍니다.     ',' 네트워크를 모니터링 하여 다운로드한 실행파일을 찾아 삭제하고 링크를 참조하여 대안을 찾아 대처합니다.',' http://www.microsoft.com/korea/technet/security/bulletin/MS09-012.mspx','CVE-2008-1436','28833','','','','alert tcp any 80 -> any any (flow:established,to_client; content:"/churrasco/-->"; nocase; content:"/churrasco/-->"; distance:0; nocase; rev:1;)');
