INSERT INTO SYSTEM_SIGHELP (LCODE, STRTITLE, STRATKTYPE, BSEVERITY, STRSUMMARY, STRDESCRIPTION, STRFALSEPOSITIVE, STRSOLUTION, STRREFERENCE, STRCVEID, STRBID, STRVUL, STRNOTVUL, STRADDRSPOOF, STRDETECT) VALUES(4861,'zeroboard xe and technote XXS filter vulnerability','Web_Access ',5,'제로보드 XXS 시도가 탐지되었습니다.','func.inc.php 파일의 stripEmbedTagForAdmin 함수의 정규식 검사가 미흡하여 XXS 공격이 가능한 취약점이 있습니다. embed 태그안에 expression함수 사용하는 경우입니다.','expression 함수의 명령어를 확인하여 악의적 의도가 있는지 확인합니다.','벤더사 업데이트가 발표되었습니다. 해당 플랫폼에 맞는 보안 패치를 진행합니다.','"http://www.xpressengine.com/
http://www.technote.co.kr/php/technote1/home.php"','','','"zeroboard XE 1.4.5.2
technote 7.2"','','','alert tcp any any -> any 80 (content:"expression"; nocase; pcre:"/\<embed[^>]*expression/i";)');
INSERT INTO SYSTEM_SIGHELP (LCODE, STRTITLE, STRATKTYPE, BSEVERITY, STRSUMMARY, STRDESCRIPTION, STRFALSEPOSITIVE, STRSOLUTION, STRREFERENCE, STRCVEID, STRBID, STRVUL, STRNOTVUL, STRADDRSPOOF, STRDETECT) VALUES(4862,'zeroboard  RFI data stream filter vulnerability','Web_Access ',5,'제로보드 원격 파일 삽입 시도가 탐지되었습니다.','데이터 스트림을 사용하해서 php 소스코드를 base64로 인코딩 전송시 include 시키며 디코딩되어 실행되는 취약점이 존재합니다. URL에 data:;와 base64를 이용하여 php 소스를 넣으면 명령이 실행되는 과정입니다.','PHP옵션 중 allow_url_include, allow_rul_fopen, register_global 옵션이 on되어 있는 경우만 성공할 수 있습니다.','벤더사 업데이트가 발표되었습니다. 해당 플랫폼에 맞는 보안 패치를 진행합니다.','http://www.xpressengine.com/','','','"zeroboard 4
zeroboard XE 1.4.4.2 below"','','','alert tcp any any -> any 80 (uricontent:"data|3a3b|"; pcre:"/(\x5fhead|ask\x5fpassword|error|login|setup)\.php[^\r\n]*(\x3f|\x26)dir\x3d[^\r\n]*data\x3a\x3b[^\r\n]*base64/i";)');
INSERT INTO SYSTEM_SIGHELP (LCODE, STRTITLE, STRATKTYPE, BSEVERITY, STRSUMMARY, STRDESCRIPTION, STRFALSEPOSITIVE, STRSOLUTION, STRREFERENCE, STRCVEID, STRBID, STRVUL, STRNOTVUL, STRADDRSPOOF, STRDETECT) VALUES(4863,'gnuboard4 LFI g4_path parameter vulnerability','Web_Access ',5,'그누보드 로컬 파일 삽입 시도가 탐지되었습니다.','그누보드의 common.php에는 g4_path라는 파라미터가 존재하는데 쿠키의 g4_path 파라미터 값에 공격 코드와 NULL 문자인 %00을 삽입함으로써 include 되는 페이지는 무시되고 공격자가 실행하려는 파일이 include됩니다.','해당 시그니처에 대한 오탐 정보가 없습니다. 오탐신고 및 시그니처에 관한 기타 문의 사항은 (주)정보보호기술  CERT 팀 (cert@infosec.co.kr) 으로 연락 주시기 바랍니다.     ','벤더사 업데이트가 발표되었습니다. 해당 플랫폼에 맞는 보안 패치를 진행합니다.','http://www.sir.co.kr/','','','gnuboard 4.33.01 below','','','alert tcp any any -> any 80 (content:"g4|5f|path"; pcre:"/^Cookie\x3a[^\r\n]+g4\x5fpath[^\r\n]+\x2500/im";)');
INSERT INTO SYSTEM_SIGHELP (LCODE, STRTITLE, STRATKTYPE, BSEVERITY, STRSUMMARY, STRDESCRIPTION, STRFALSEPOSITIVE, STRSOLUTION, STRREFERENCE, STRCVEID, STRBID, STRVUL, STRNOTVUL, STRADDRSPOOF, STRDETECT) VALUES(4864,'zeroboad user auth and session process vulnerability','Web_Access ',5,'제로보드의 인증 및 세션처리 취약점을 악용하는 시도가 탐지되었습니다.','제로보드에서 사용자의 인증 및 세션처리 과정에서 권한상승을 시킬 수 있고 악성코드를 설치할 수 있는 취약점이 존재합니다. admin_setup.php에 접근시 변수에 특정값을 입력하여 전송하므로써 관리자 권한을 획득할 수 있습니다.','해당 시그니처에 대한 오탐 정보가 없습니다. 오탐신고 및 시그니처에 관한 기타 문의 사항은 (주)정보보호기술  CERT 팀 (cert@infosec.co.kr) 으로 연락 주시기 바랍니다.     ','벤더사 업데이트가 발표되었습니다. 해당 플랫폼에 맞는 보안 패치를 진행합니다.','http://www.xpressengine.com/','','','zeroboard','','','alert tcp any any -> any 80 (uricontent:"admin|5f|setup.php"; content:"is|5f|admin"; nocase; pcre:"/is\x5fadmin\s*\x3d\s*1/i";)');
INSERT INTO SYSTEM_SIGHELP (LCODE, STRTITLE, STRATKTYPE, BSEVERITY, STRSUMMARY, STRDESCRIPTION, STRFALSEPOSITIVE, STRSOLUTION, STRREFERENCE, STRCVEID, STRBID, STRVUL, STRNOTVUL, STRADDRSPOOF, STRDETECT) VALUES(4865,'Adobe PDF javascript execution of go to action','Web_Access ',5,'Go-To 액션으로 javascript를 실행하는 행위가 탐지되었습니다.','PDF 파일은 외부 PDF 파일안의 목적지로 점프하는 원격 go-to 액션을 포함할 수 있습니다. 목표 파일 문자열이 적절하게 유효성검사가 되지 않거나 javascript 를 포함할 수 있습니다. 웹 사이트로 부터 PDF 파일이 열리거나 브라우저에서 보여질 때, PDF 파일은 javascript를 실행할 수 있습니다.','"PDF 파일의 object에서 해당 탐지 문자열들이 dictionary 안에 포함되어야 합니다. << 와 >> 사이에 존재해야 합니다. 다음은 예제입니다.

5 0 obj
<<
/S /GoToR
/F (javascript:alert(document.domain))
/NewWindow false
>>
endobj"','"보안 업데이트를 진행합니다.
백신을 최신으로 업데이트하여 검사합니다."','"http://cve.mitre.org/cgi-bin/cvename.cgi?name=CVE-2011-2101
http://www.adobe.com/support/security/bulletins/apsb11-16.html"','CVE-2011-2101','','"Adobe Reader X (10.0.1) and earlier 10.x versions for Windows
Adobe Reader X (10.0.3) and earlier 10.x versions for Macintosh
Adobe Reader 9.4.4 and earlier 9.x versions for Windows and Macintosh
Adobe Reader 8.2.6 and earlier 8.x versions for Windows and Macintosh
Adobe Acrobat X (10.0.3) and earlier 10.x versions for Windows and Macintosh
Adobe Acrobat 9.4.4 and earlier 9.x versions for Windows and Macintosh
Adobe Acrobat 8.2.6 and earlier 8.x versions for Windows and Macintosh"','','','alert tcp any 80 -> any any (content:"|2f|GoToR"; nocase; pcre:"/\/S\s*\/GoToR[^>]*\/F\s*[^\r\n\x3e]+\(javascript\s*\x3a/i";)');
INSERT INTO SYSTEM_SIGHELP (LCODE, STRTITLE, STRATKTYPE, BSEVERITY, STRSUMMARY, STRDESCRIPTION, STRFALSEPOSITIVE, STRSOLUTION, STRREFERENCE, STRCVEID, STRBID, STRVUL, STRNOTVUL, STRADDRSPOOF, STRDETECT) VALUES(4866,'MS IE Drag and Drop Memory Corruption Vulnerability','Web_Access ',5,'IE 메모리 참조 오류가 발생하였습니다.','Drag 이벤트 핸들러안에 datatransfer.effectallowed 값이 설정되고 URL 이 setdata로 설정되었을 때 해당 이벤트가 탐지됩니다. ','"다음은 예제입니다.
<HEAD>
<SCRIPT>
function InitDrag()
{
    event.dataTransfer.effectAllowed=""all"";
    event.dataTransfer.setData(""URL"", ""www.infosec.co.kr"");
}
</SCRIPT>
</HEAD>
<BODY>
 <B ondragstart=""InitDrag()"" >AAA</B>
 <Input type=""text"" value=""OK""></input>
 </BODY>
Text AAA를 클릭하고 드레그하여 텍스트 박스로 드랍하면 IE 충돌이 일어난다.
이 이벤트가 탐지될 경우 Drag 이벤트 헨들러 안에 존재하는 지 살펴보고 삽입된 URL이 악성인지 판단해야 합니다. 사용자의 행위가 들어가기 때문에 모든 탐지가 악성이 아닐 수 있습니다."','"보안 업데이트를 진행합니다.
백신을 최신으로 업데이트하여 검사합니다."','"http://cve.mitre.org/cgi-bin/cvename.cgi?name=CVE-2011-1254
http://www.microsoft.com/technet/security/Bulletin/MS11-050.mspx"','CVE-2011-1254','','"Internet Explorer 6
Internet Explorer 7
Internet Explorer 8
Internet Explorer 9"','','','alert tcp any 80 -> any any (content:"dataTransfer.effectAllowed"; nocase; pcre:"/dataTransfer\.effectAllowed[^\x7d]+setData\s*\x28\s*(\x22|\x27)\s*URL\s*(\x22|\x27)/i";)');
INSERT INTO SYSTEM_SIGHELP (LCODE, STRTITLE, STRATKTYPE, BSEVERITY, STRSUMMARY, STRDESCRIPTION, STRFALSEPOSITIVE, STRSOLUTION, STRREFERENCE, STRCVEID, STRBID, STRVUL, STRNOTVUL, STRADDRSPOOF, STRDETECT) VALUES(4867,'MS IE Layout Memory Corruption Vulnerability','Web_Access ',5,'IE 메모리 참조 오류가 발생하였습니다.','비정상적인 레이아웃을 만날 때 핸들러는 에러를 발생합니다. layout-grid-char 값이 9999가 넘는 경우 해당 이벤트가 발생합니다.','"PoC는 다음과 같습니다.

<meta http-equiv=""refresh"" content=""10"">
<STYLE>
    object{
        float: left;
    }
</STYLE>
<acronym>

</acronym>
<object>
    head
</object>

<div style = \'layout-grid-char: 999999ex;\'>
    aaaaaa
</div>
<table>
<col>
    aaa
</col>
</table>

Layout-grid-char 값이 9999가 넘는경우 비정상으로 처리됩니다. 해당 CVE 이슈를 탐지하기 위해서는 이 코드가 핵심적이지만 잠재적으로 layout-grid-char 값을 사용하지 않고 취약점을 발생시킬수 있는 로직이 존재할 수 있습니다."','"보안 업데이트를 진행합니다.
백신을 최신으로 업데이트하여 검사합니다."','"http://cve.mitre.org/cgi-bin/cvename.cgi?name=CVE-2011-1260
http://www.microsoft.com/technet/security/Bulletin/MS11-050.mspx"','CVE-2011-1260','','"Internet Explorer 6
Internet Explorer 7
Internet Explorer 8
Internet Explorer 9"','','','alert tcp any 80 -> any any (content:"layout-grid-char"; nocase; pcre:"/layout\x2dgrid\x2dchar\x3a\s*[1-9][0-9]{4}/i";)');
